import React, { useEffect, useState } from 'react'
import axios from 'axios'
import {Link} from 'react-router-dom'

export default function EmpTable() {
 
  const [employees,setEmployees]=useState([]);

  useEffect(()=>{
      axios.get("http://localhost:3006/emp/det").then((res)=>{setEmployees(res.data);console.log(res.data)}).catch((err)=>console.log(err))
  },[]); 


  function del(id){
      if(window.confirm("are you sure want to delete")){
        axios.delete("http://localhost:3006/emp/"+id)
        .then((res)=>{ 
            
            if(res.data.success){

              alert("deleted successfully");
              axios.get("http://localhost:3006/emp/det").then((res)=>{setEmployees(res.data);console.log(res.data)}).catch((err)=>console.log(err));

             }
            }).catch((err)=>console.log(err))
      }
  }
  

  return (
    <>
     <div className='container-fluid p-1'>
        <div className='d-flex justify-content-between'>
            <h2>Employees</h2>
            <div>
                <input type="text" placeholder='' className='rounded border-secondary me-2'/>
                <Link to="/insert" className="btn btn-primary">Add new Employees</Link>
            </div>  
        </div>
        <div>
            <table class="table border rounded-2">
                <thead>
                    <tr>
                    <th scope="col">Employee Name</th>
                    <th scope="col">ID</th>
                    <th scope="col">Department</th>
                    <th scope="col">Designation</th>
                    <th scope="col">Project</th>
                    <th scope="col">Type</th>
                    <th scope="col">Status</th>
                    <th scope="col">Action</th>
                    </tr>
                </thead>
                <tbody>

                    { employees.length === 0 ? <tr className='d-flex justify-content-center align-items-center'><td ><h3 className='my-3 p-2 '>No Data Found</h3></td></tr> : 
                     employees.map((val)=>{
                        return(
                           <tr key={val.emp_id}>
                            <td className='d-flex align-items-center gap-2'>
                                <img src={"http://localhost:3006"+val.image} className='img-fluid rounded-circle'  alt='employee' width="30px" height="30px"></img>
                                {val.name}
                            </td>
                            <td>{val.emp_id}</td>
                            <td>{val.department}</td>
                            <td>{val.designation}</td>
                            <td>{val.project}</td>
                            <td>{val.type}</td>
                            <td>{val.status}</td>
                            <td>
                                <Link to={"/view/"+val.emp_id} className='btn btn-outline-secondary'>
                                    <svg xmlns="http://www.w3.org/2000/svg" width="16" height="16" fill="currentColor" class="bi bi-eye" viewBox="0 0 16 16">
                                    <path d="M16 8s-3-5.5-8-5.5S0 8 0 8s3 5.5 8 5.5S16 8 16 8M1.173 8a13 13 0 0 1 1.66-2.043C4.12 4.668 5.88 3.5 8 3.5s3.879 1.168 5.168 2.457A13 13 0 0 1 14.828 8q-.086.13-.195.288c-.335.48-.83 1.12-1.465 1.755C11.879 11.332 10.119 12.5 8 12.5s-3.879-1.168-5.168-2.457A13 13 0 0 1 1.172 8z"/>
                                    <path d="M8 5.5a2.5 2.5 0 1 0 0 5 2.5 2.5 0 0 0 0-5M4.5 8a3.5 3.5 0 1 1 7 0 3.5 3.5 0 0 1-7 0"/>
                                    </svg>
                                </Link>
                                <Link to={"/update/"+val.emp_id} >Update</Link>
                                <button onClick={()=>del(val.emp_id)}>del</button>
                            </td>
                           </tr>
                        );
                    })
                    
                    }
                </tbody>
                </table>
        </div>
     </div>
    </>
  )
}
